#!/usr/bin/env node
const program = require('commander');
const { name: pkgName, version } = require('./package.json');
const init = require('./lib/init');

program.version(version).description(`
${pkgName} 是，提供简单的 CLI API，让工程能够一键接入，并为工程配置 git commit 卡点，降低工程实施规约的成本
`);

program
  .command('init')
  .description('一键接入：为工程初始化规约工具和配置，可以根据工程类型和需求进行定制')
  .action(init);

program.parse(process.argv);
